  background: linear-gradient(135deg, 
    hsl(142 72% 29%) 0%, 
    hsl(45 86% 58%) 100%
  );
  box-shadow: 0 4px 20px rgba(34, 197, 94, 0.3);
}

/* Custom scrollbar for chat */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: hsl(var(--neutral-100));
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: hsl(var(--neutral-300));
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--neutral-400));
}

/* Animation utilities */
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.animate-fade-in {
  animation: fadeIn 0.3s ease-out;
}

/* Professional shadow utilities */
.shadow-professional {
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
}

.shadow-professional-lg {
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
}

/* Status badge animations */
.status-badge {
  @apply inline-flex items-center px-2 py-1 rounded-full text-xs font-medium transition-colors;
}

.status-pending {
  @apply bg-yellow-100 text-yellow-800;
}

.status-approved {
  @apply bg-green-100 text-green-800;
}

.status-rejected {
  @apply bg-red-100 text-red-800;
}

/* Professional card hover effects */
.card-hover {
  @apply transition-all duration-200 hover:shadow-professional-lg hover:-translate-y-1;
}

/* Loading states */
.loading-shimmer {
  background: linear-gradient(90deg, hsl(var(--neutral-200)) 25%, hsl(var(--neutral-100)) 50%, hsl(var(--neutral-200)) 75%);
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
}

@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

