import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Send, MessageCircle, Users, Circle } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface ChatUser {
  id: string;
  email: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
  role: 'admin' | 'partner';
}

interface ChatMessage {
  id: string;
  senderId: string;
  receiverId: string;
  message: string;
  isRead: boolean;
  createdAt: string;
  sender: ChatUser;
}

interface ChatSystemProps {
  className?: string;
}

export function ChatSystem({ className }: ChatSystemProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedUser, setSelectedUser] = useState<ChatUser | null>(null);
  const [message, setMessage] = useState("");
  const [socket, setSocket] = useState<WebSocket | null>(null);
  const [onlineUsers, setOnlineUsers] = useState<Set<string>>(new Set());
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Get available chat users - filtered by role
  const { data: chatUsers = [] } = useQuery<ChatUser[]>({
    queryKey: ['/api/chat/users'],
    enabled: !!user,
  });

  // Get unread message counts
  const { data: unreadCounts = {} } = useQuery<Record<string, number>>({
    queryKey: ['/api/chat/unread-counts'],
    enabled: !!user,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Get chat messages for selected user
  const { data: messages = [], refetch } = useQuery<ChatMessage[]>({
    queryKey: ['/api/chat/messages', selectedUser?.id],
    enabled: !!user && !!selectedUser,
    refetchInterval: 2000, // Auto refresh every 2 seconds
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (newMessage: { receiverId: string; message: string }) => {
      const response = await fetch('/api/chat/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(newMessage),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json();
    },
    onSuccess: (data) => {
      console.log('Xabar muvaffaqiyatli yuborildi:', data);
      setMessage("");

      // Refresh messages after a short delay to ensure new message is included
      setTimeout(() => {
        queryClient.invalidateQueries({
          queryKey: ['/api/chat/messages', selectedUser?.id],
        });
        queryClient.invalidateQueries({
          queryKey: ['/api/chat/unread-counts'],
        });
      }, 100);
    },
    onError: (error: Error) => {
      console.error('Xabar yuborishda xatolik:', error);
      toast({
        title: "Xato",
        description: "Xabar yuborilmadi. Qaytadan urinib ko'ring.",
        variant: "destructive",
      });
    },
  });

  // Mark messages as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: async (senderId: string) => {
      const response = await fetch(`/api/chat/mark-read/${senderId}`, {
        method: 'PUT',
        credentials: 'include',
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({
        queryKey: ['/api/chat/unread-counts'],
      });
    },
  });

  // WebSocket connection
  useEffect(() => {
    if (!user) return;

    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;
    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      ws.send(JSON.stringify({ type: 'auth', userId: user.id }));
      setSocket(ws);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'new_message') {
        // Refresh messages if it's for the selected user
        if (selectedUser && data.message.senderId === selectedUser.id) {
          queryClient.invalidateQueries({
            queryKey: ['/api/chat/messages', selectedUser.id],
          });
        }

        // Refresh unread counts
        queryClient.invalidateQueries({
          queryKey: ['/api/chat/unread-counts'],
        });

        // Show toast notification
        toast({
          title: "New Message",
          description: `From ${data.message.sender?.firstName || data.message.sender?.email}`,
        });
      }

      if (data.type === 'user_online') {
        setOnlineUsers(prev => new Set(prev).add(data.userId));
      }

      if (data.type === 'user_offline') {
        setOnlineUsers(prev => {
          const newSet = new Set(prev);
          newSet.delete(data.userId);
          return newSet;
        });
      }
    };

    ws.onclose = () => {
      setSocket(null);
    };

    return () => {
      ws.close();
    };
  }, [user, selectedUser, queryClient, toast]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Mark messages as read when selecting a user
  useEffect(() => {
    if (selectedUser && unreadCounts[selectedUser.id] > 0) {
      markAsReadMutation.mutate(selectedUser.id);
    }
  }, [selectedUser, unreadCounts]);

  const handleSendMessage = () => {
    if (!message.trim() || !selectedUser || sendMessageMutation.isPending) return;

    const messageToSend = message.trim();

    sendMessageMutation.mutate({
      receiverId: selectedUser.id,
      message: messageToSend,
    });
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const formatMessageTime = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));

    if (days === 0) {
      return date.toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' });
    } else if (days === 1) {
      return 'Yesterday';
    } else {
      return date.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' });
    }
  };

  const getUserDisplayName = (user: ChatUser) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    return user.email.split('@')[0];
  };

  const getUserInitials = (user: ChatUser) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`;
    }
    return user.email.substring(0, 2).toUpperCase();
  };

  if (!user) {
    return (
      <Card className={`premium-card border-slate-600 ${className}`}>
        <CardContent className="flex items-center justify-center py-8">
          <p className="text-white">Chat tizimiga kirish uchun tizimga kiring</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`h-[600px] flex flex-col premium-card border-slate-600 ${className}`} data-testid="chat-system">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2 text-white">
          <MessageCircle className="h-5 w-5 text-blue-400" />
          Chat Tizimi
        </CardTitle>
        <CardDescription className="text-slate-300">
          {user.role === 'admin' ? 'Hamkorlar bilan chat' : 'Admin bilan chat'}
        </CardDescription>
      </CardHeader>

      <CardContent className="flex-1 flex gap-4 p-4 min-h-0">
        {/* Users List */}
        <div className="w-1/3 min-w-0">
          <div className="flex items-center gap-2 mb-3">
            <Users className="h-4 w-4 text-blue-400" />
            <span className="text-sm font-medium text-white">{user.role === 'admin' ? 'Hamkorlar' : 'Adminlar'}</span>
          </div>

          <ScrollArea className="h-full">
            <div className="space-y-2">
              {chatUsers.map((chatUser) => (
                <div
                  key={chatUser.id}
                  onClick={() => setSelectedUser(chatUser)}
                  className={`p-3 rounded-lg cursor-pointer transition-colors ${
                    selectedUser?.id === chatUser.id
                      ? 'bg-blue-600/20 border border-blue-500'
                      : 'hover:bg-slate-700/50'
                  }`}
                  data-testid={`user-${chatUser.id}`}
                >
                  <div className="flex items-center gap-3">
                    <div className="relative">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={chatUser.profileImageUrl} />
                        <AvatarFallback className="text-xs">
                          {getUserInitials(chatUser)}
                        </AvatarFallback>
                      </Avatar>
                      {onlineUsers.has(chatUser.id) && (
                        <Circle className="absolute -bottom-1 -right-1 h-3 w-3 fill-green-500 text-green-500" />
                      )}
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <p className="text-sm font-medium truncate text-white">
                          {getUserDisplayName(chatUser)}
                        </p>
                        {unreadCounts[chatUser.id] > 0 && (
                          <Badge variant="destructive" className="text-xs px-1 py-0 min-w-[1.25rem] h-5 bg-red-500">
                            {unreadCounts[chatUser.id]}
                          </Badge>
                        )}
                      </div>
                      <p className="text-xs text-slate-400 capitalize">
                        {chatUser.role === 'admin' ? 'Administrator' : 'Hamkor'}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </ScrollArea>
        </div>

        <Separator orientation="vertical" className="bg-slate-600" />

        {/* Chat Area */}
        <div className="flex-1 flex flex-col min-h-0">
          {selectedUser ? (
            <>
              {/* Chat Header */}
              <div className="flex items-center gap-3 pb-3">
                <Avatar className="h-8 w-8">
                  <AvatarImage src={selectedUser.profileImageUrl} />
                  <AvatarFallback className="text-xs bg-slate-600 text-white">
                    {getUserInitials(selectedUser)}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <p className="font-medium text-white">{getUserDisplayName(selectedUser)}</p>
                  <p className="text-xs text-slate-400 flex items-center gap-1">
                    {onlineUsers.has(selectedUser.id) ? (
                      <>
                        <Circle className="h-2 w-2 fill-green-400 text-green-400" />
                        Online
                      </>
                    ) : (
                      <>
                        <Circle className="h-2 w-2 fill-gray-400 text-gray-400" />
                        Offline
                      </>
                    )}
                  </p>
                </div>
              </div>

              <Separator className="mb-3 bg-slate-600" />

              {/* Messages */}
              <ScrollArea className="flex-1 mb-3">
                <div className="space-y-3 pr-4">
                  {messages.map((msg) => (
                    <div
                      key={msg.id}
                      className={`flex ${msg.senderId === user.id ? 'justify-end' : 'justify-start'}`}
                    >
                      <div
                        className={`max-w-[80%] rounded-lg px-3 py-2 ${
                          msg.senderId === user.id
                            ? 'bg-blue-600 text-white'
                            : 'bg-slate-700 text-white border border-slate-600'
                        }`}
                        data-testid={`message-${msg.id}`}
                      >
                        <p className="text-sm text-white">{msg.message}</p>
                        <p className="text-xs opacity-70 mt-1 text-slate-300">
                          {formatMessageTime(msg.createdAt)}
                        </p>
                      </div>
                    </div>
                  ))}
                  <div ref={messagesEndRef} />
                </div>
              </ScrollArea>

              {/* Message Input */}
              <div className="flex gap-2">
                <Input
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Xabar yozing..."
                  className="bg-slate-800 border-slate-600 text-white placeholder:text-slate-400"
                  disabled={sendMessageMutation.isPending}
                  data-testid="input-message"
                />
                <Button
                  onClick={handleSendMessage}
                  disabled={!message.trim() || sendMessageMutation.isPending}
                  size="icon"
                  className="marketplace-gradient"
                  data-testid="button-send"
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center text-slate-400">
                <MessageCircle className="h-12 w-12 mx-auto mb-2 opacity-50 text-slate-500" />
                <p className="text-white">Suhbat boshlash uchun foydalanuvchini tanlang</p>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/commission-calcula.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { TrendingUp, Target, Star, Calculator } from "lucide-react";

interface CommissionResult {
  tier: number;
  fixedPayment: number;
  bonusPayment: number;
  totalPayment: number;
  salesThreshold: number;
  bonusPercentage: number;
  nextTierAt: number | null;
}

interface CommissionCalculatorProps {
  className?: string;
}

export function CommissionCalculator({ className }: CommissionCalculatorProps) {
  const [salesInput, setSalesInput] = useState<string>("10,000,000");
  const [result, setResult] = useState<CommissionResult | null>(null);

  // Format number input with commas and handle som currency
  const formatNumberInput = (value: string): string => {
    // Remove non-digit characters
    const numbers = value.replace(/\D/g, '');

    // Remove leading zeros but keep at least one digit
    const cleanNumbers = numbers.replace(/^0+/, '') || '0';

    // Add commas for thousands separator
    return cleanNumbers.replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  };

  const parseNumberInput = (value: string): number => {
    return parseInt(value.replace(/,/g, '') || '0', 10);
  };

  const handleInputChange = (value: string) => {
    const formatted = formatNumberInput(value);
    setSalesInput(formatted);
  };

  // Calculate commission based on corrected 3-tier system
  const calculateCommission = (sales: number): CommissionResult => {
    // Tier 1: 2.5M som fixed + 15M som threshold for bonus
    if (sales < 15000000) {
      return {
        tier: 1,
        fixedPayment: 2500000,
        bonusPayment: 0,
        totalPayment: 2500000,
        salesThreshold: 15000000,
        bonusPercentage: 0,
        nextTierAt: 15000000,
      };
    }

    // Tier 1 with bonus: 15M+ sales
    if (sales >= 15000000 && sales < 50000000) {
      const bonus = (sales - 15000000) * 0.15;
      return {
        tier: 1,
        fixedPayment: 2500000,
        bonusPayment: bonus,
        totalPayment: 2500000 + bonus,
        salesThreshold: 15000000,
        bonusPercentage: 15,
        nextTierAt: 50000000,
      };
    }

    // Tier 2: 4.5M som fixed + 50M som threshold for bonus
    if (sales >= 50000000 && sales < 80000000) {
      const bonus = (sales - 50000000) * 0.20;
      return {
        tier: 2,
        fixedPayment: 4500000,
        bonusPayment: bonus,
        totalPayment: 4500000 + bonus,
        salesThreshold: 50000000,
        bonusPercentage: 20,
        nextTierAt: 80000000,
      };
    }

    // Tier 3: 6M som fixed + 80M som threshold for bonus
    if (sales >= 80000000) {
      const bonus = (sales - 80000000) * 0.25;
      return {
        tier: 3,
        fixedPayment: 6000000,
        bonusPayment: bonus,
        totalPayment: 6000000 + bonus,
        salesThreshold: 80000000,
        bonusPercentage: 25,
        nextTierAt: null,
      };
    }

    // Default fallback
    return {
      tier: 1,
      fixedPayment: 2500000,
      bonusPayment: 0,
      totalPayment: 2500000,
      salesThreshold: 15000000,
      bonusPercentage: 0,
      nextTierAt: 15000000,
    };
  };

  useEffect(() => {
    const sales = parseNumberInput(salesInput);
    const calculatedResult = calculateCommission(sales);
    setResult(calculatedResult);
  }, [salesInput]);

  const formatSom = (amount: number): string => {
    return new Intl.NumberFormat('uz-UZ').format(amount) + ' so\'m';
  };

  const getTierInfo = (tier: number) => {
    switch (tier) {
      case 1:
        return {
          name: 'Tier 1',
          color: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
          icon: <Target className="h-4 w-4" />,
          description: '2.5M so\'m fiksa + 15M so\'mdan bonus'
        };
      case 2:
        return {
          name: 'Tier 2',
          color: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
          icon: <TrendingUp className="h-4 w-4" />,
          description: '4.5M so\'m fiksa + 50M so\'mdan bonus'
        };
      case 3:
        return {
          name: 'Tier 3',
          color: 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300',
          icon: <Star className="h-4 w-4" />,
          description: '6M so\'m fiksa + 80M so\'mdan bonus'
        };
      default:
        return {
          name: 'Tier 1',
          color: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
          icon: <Target className="h-4 w-4" />,
          description: '2.5M so\'m fiksa + 15M so\'mdan bonus'
        };
    }
  };

  const sales = parseNumberInput(salesInput);
  const tierInfo = result ? getTierInfo(result.tier) : getTierInfo(1);

  // Calculate progress to next tier
  const progressToNext = result?.nextTierAt 
    ? Math.min((sales / result.nextTierAt) * 100, 100)
    : 100;

  return (
    <Card className={className} data-testid="commission-calculator">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2">
          <Calculator className="h-5 w-5" />
          Komissiya Kalkulyatori
        </CardTitle>
        <CardDescription>
          Oylik savdo hajmingizni kiriting va to'lov miqdorini hisoblang
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Sales Input */}
        <div className="space-y-2">
          <Label htmlFor="sales-input" className="text-sm font-medium">
            Oylik Savdo Hajmi (so'm)
          </Label>
          <Input
            id="sales-input"
            type="text"
            value={salesInput}
            onChange={(e) => handleInputChange(e.target.value)}
            placeholder="10,000,000"
            className="text-lg font-mono"
            data-testid="input-sales-amount"
          />
        </div>

        {result && (
          <>
            {/* Current Tier */}
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <span className="text-sm text-muted-foreground">Joriy Tier:</span>
                <Badge className={tierInfo.color}>
                  {tierInfo.icon}
                  <span className="ml-1">{tierInfo.name}</span>
                </Badge>
              </div>
            </div>

            {/* Tier Description */}
            <div className="bg-muted/50 rounded-lg p-3">
              <p className="text-sm text-muted-foreground">{tierInfo.description}</p>
            </div>

            {/* Progress to Next Tier */}
            {result.nextTierAt && (
              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-muted-foreground">Keyingi Tier uchun:</span>
                  <span className="font-medium">
                    {formatSom(result.nextTierAt)}
                  </span>
                </div>
                <Progress value={progressToNext} className="h-2" />
                <p className="text-xs text-muted-foreground">
                  Qolgan: {formatSom(Math.max(0, result.nextTierAt - sales))}
                </p>
              </div>
            )}

            <Separator />

            {/* Payment Breakdown */}
            <div className="space-y-4">
              <h4 className="font-medium text-sm">To'lov Tafsilotlari:</h4>

              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">Fiksatsiyalangan to'lov:</span>
                  <span className="font-medium">{formatSom(result.fixedPayment)}</span>
                </div>

                <div className="flex justify-between">
                  <span className="text-sm text-muted-foreground">
                    Bonus to'lov ({result.bonusPercentage}%):
                  </span>
                  <span className="font-medium">{formatSom(result.bonusPayment)}</span>
                </div>

                <Separator />

                <div className="flex justify-between items-center">
                  <span className="font-medium">Jami to'lov:</span>
                  <span className="text-lg font-bold text-primary">
                    {formatSom(result.totalPayment)}
                  </span>
                </div>
              </div>
            </div>

            {/* Bonus System Explanation */}
            {result.bonusPayment === 0 && sales < result.salesThreshold && (
              <div className="bg-orange-50 border border-orange-200 rounded-lg p-3">
                <p className="text-sm text-orange-800">
                  ðŸ’¡ Bonus sistema ishlamaydi: {formatSom(result.salesThreshold)} dan yuqori 
                  savdo qilsangiz bonus olasiz!
                </p>
              </div>
            )}

            {result.bonusPayment > 0 && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                <p className="text-sm text-green-800">
                  ðŸŽ‰ Bonus sistema faol! Siz {formatSom(result.salesThreshold)} dan yuqori 
                  savdo qildingiz va {result.bonusPercentage}% bonus olasiz.
                </p>
              </div>
            )}
          </>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/enhanced-product-r.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, AlertCircle, Package, Truck, X, Upload, Image } from "lucide-react";

interface ProductItem {
  id: string;
  productName: string;
  description: string;
  expectedQuantity: string;
  estimatedPrice: string;
  imageFile?: File;
  imagePreview?: string;
}

export function EnhancedProductRequestForm() {
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [products, setProducts] = useState<ProductItem[]>([]);
  const [supplierInfo, setSupplierInfo] = useState("");
  const [urgencyLevel, setUrgencyLevel] = useState("normal");

  const [currentProduct, setCurrentProduct] = useState<ProductItem>({
    id: "",
    productName: "",
    description: "",
    expectedQuantity: "",
    estimatedPrice: "",
  });

  const createRequestMutation = useMutation({
    mutationFn: async (data: { products: ProductItem[]; supplierInfo: string; urgencyLevel: string }) => {
      // Create bulk request with all products
      return apiRequest("POST", "/api/product-requests/bulk", data);
    },
    onSuccess: () => {
      toast({
        title: "So'rovlar yuborildi",
        description: `${products.length} ta mahsulot so'rovi muvaffaqiyatli yuborildi. Admin ko'rib chiqadi.`,
      });
      // Reset form
      setProducts([]);
      setSupplierInfo("");
      setUrgencyLevel("normal");
      queryClient.invalidateQueries({ queryKey: ["/api/product-requests"] });
    },
    onError: (error) => {
      toast({
        title: "Xato",
        description: "So'rovlarni yuborishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  const addProduct = () => {
    if (!currentProduct.productName || !currentProduct.expectedQuantity) {
      toast({
        title: "Ma'lumot yetishmaydi",
        description: "Mahsulot nomi va miqdorni kiriting",
        variant: "destructive",
      });
      return;
    }

    const newProduct = {
      ...currentProduct,
      id: Date.now().toString(),
    };

    setProducts([...products, newProduct]);
    setCurrentProduct({
      id: "",
      productName: "",
      description: "",
      expectedQuantity: "",
      estimatedPrice: "",
    });
    setIsDialogOpen(false);
  };

  const removeProduct = (id: string) => {
    setProducts(products.filter(p => p.id !== id));
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) { // 5MB limit
        toast({
          title: "Fayl hajmi katta",
          description: "Rasm hajmi 5MB dan kichik bo'lishi kerak",
          variant: "destructive",
        });
        return;
      }

      const reader = new FileReader();
      reader.onload = (event) => {
        setCurrentProduct({
          ...currentProduct,
          imageFile: file,
          imagePreview: event.target?.result as string,
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmitAll = () => {
    if (products.length === 0) {
      toast({
        title: "Mahsulot yo'q",
        description: "Kamida bitta mahsulot qo'shing",
        variant: "destructive",
      });
      return;
    }

    createRequestMutation.mutate({
      products,
      supplierInfo,
      urgencyLevel,
    });
  };

  const urgencyColors = {
    low: "bg-blue-100 text-blue-800",
    normal: "bg-green-100 text-green-800", 
    high: "bg-orange-100 text-orange-800",
    urgent: "bg-red-100 text-red-800"
  };

  return (
    <div className="space-y-6">
      {/* Supplier Information (Common for all products) */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Truck className="h-5 w-5" />
            <span>Taminotchi Ma'lumotlari</span>
          </CardTitle>
          <CardDescription>
            Barcha mahsulotlar uchun umumiy taminotchi ma'lumotlari
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="supplierInfo">Taminotchi haqida ma'lumot</Label>
            <Textarea
              id="supplierInfo"
              value={supplierInfo}
              onChange={(e) => setSupplierInfo(e.target.value)}
              placeholder="Kompaniya nomi, manzil, telefon raqam, boshqa ma'lumotlar..."
              rows={3}
              data-testid="textarea-supplier-info"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="urgencyLevel">Umumiy muhimlik darajasi</Label>
            <Select value={urgencyLevel} onValueChange={setUrgencyLevel}>
              <SelectTrigger data-testid="select-urgency-level">
                <SelectValue placeholder="Muhimlik darajasini tanlang" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="low">Kam muhim</SelectItem>
                <SelectItem value="normal">O'rtacha</SelectItem>
                <SelectItem value="high">Muhim</SelectItem>
                <SelectItem value="urgent">Juda muhim</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Product List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Package className="h-5 w-5" />
              <span>Mahsulotlar Ro'yxati ({products.length})</span>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button size="sm" data-testid="button-add-product">
                  <Plus className="h-4 w-4 mr-2" />
                  Mahsulot Qo'shish
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl">
                <DialogHeader>
                  <DialogTitle>Yangi Mahsulot Qo'shish</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="productName">Mahsulot nomi *</Label>
                      <Input
                        id="productName"
                        value={currentProduct.productName}
                        onChange={(e) => setCurrentProduct({ ...currentProduct, productName: e.target.value })}
                        placeholder="Mahsulot nomini kiriting"
                        data-testid="input-product-name"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="expectedQuantity">Kutilayotgan miqdor *</Label>
                      <Input
                        id="expectedQuantity"
                        type="number"
                        value={currentProduct.expectedQuantity}
                        onChange={(e) => setCurrentProduct({ ...currentProduct, expectedQuantity: e.target.value })}
                        placeholder="Miqdorni kiriting"
                        data-testid="input-expected-quantity"
                      />
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="description">Batafsil tavsif</Label>
                    <Textarea
                      id="description"
                      value={currentProduct.description}
                      onChange={(e) => setCurrentProduct({ ...currentProduct, description: e.target.value })}
                      placeholder="Mahsulot haqida batafsil ma'lumot"
                      rows={3}
                      data-testid="textarea-description"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="estimatedPrice">Tan narx (so'm)</Label>
                    <Input
                      id="estimatedPrice"
                      type="number"
                      value={currentProduct.estimatedPrice}
                      onChange={(e) => setCurrentProduct({ ...currentProduct, estimatedPrice: e.target.value })}
                      placeholder="Tan narxni kiriting"
                      data-testid="input-estimated-price"
                    />
                  </div>

                  {/* Image Upload */}
                  <div className="space-y-2">
                    <Label htmlFor="productImage">Mahsulot rasmi (ixtiyoriy)</Label>
                    <div className="flex items-center space-x-4">
                      <Input
                        id="productImage"
                        type="file"
                        accept="image/*"
                        onChange={handleImageUpload}
                        className="hidden"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => document.getElementById('productImage')?.click()}
                        className="flex items-center space-x-2"
                      >
                        <Upload className="h-4 w-4" />
                        <span>Rasm tanlash</span>
                      </Button>
                      {currentProduct.imagePreview && (
                        <div className="relative">
                          <img
                            src={currentProduct.imagePreview}
                            alt="Preview"
                            className="h-16 w-16 object-cover rounded-lg border"
                          />
                          <Button
                            type="button"
                            variant="destructive"
                            size="sm"
                            className="absolute -top-2 -right-2 h-6 w-6 rounded-full p-0"
                            onClick={() => setCurrentProduct({ ...currentProduct, imageFile: undefined, imagePreview: undefined })}
                          >
                            <X className="h-3 w-3" />
                          </Button>
                        </div>
                      )}
                    </div>
                  </div>

                  <div className="flex justify-end space-x-2">
                    <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)}>
                      Bekor qilish
                    </Button>
                    <Button type="button" onClick={addProduct}>
                      Qo'shish
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {products.length === 0 ? (
            <div className="text-center py-8 text-neutral-500">
              <Package className="h-12 w-12 mx-auto mb-4 text-neutral-300" />
              <p>Hali mahsulot qo'shilmagan</p>
              <p className="text-sm">Yuqoridagi tugma orqali mahsulot qo'shing</p>
            </div>
          ) : (
            <div className="space-y-3">
              {products.map((product) => (
                <div key={product.id} className="flex items-center justify-between p-4 border border-neutral-200 rounded-lg">
                  <div className="flex items-center space-x-4">
                    {product.imagePreview && (
                      <img
                        src={product.imagePreview}
                        alt={product.productName}
                        className="h-12 w-12 object-cover rounded-lg border"
                      />
                    )}
                    <div className="flex-1">
                      <h4 className="font-medium text-neutral-900">{product.productName}</h4>
                      <p className="text-sm text-neutral-600">Miqdor: {product.expectedQuantity}</p>
                      {product.estimatedPrice && (
                        <p className="text-sm text-neutral-600">Narx: {Number(product.estimatedPrice).toLocaleString()} so'm</p>
                      )}
                      {product.description && (
                        <p className="text-xs text-neutral-500 mt-1">{product.description}</p>
                      )}
                    </div>
                  </div>
                  <Button
                    type="button"
                    variant="destructive"
                    size="sm"
                    onClick={() => removeProduct(product.id)}
                    data-testid={`button-remove-product-${product.id}`}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Submit Section */}
      {products.length > 0 && (
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Badge className={urgencyColors[urgencyLevel as keyof typeof urgencyColors]}>
                  {urgencyLevel === 'low' && 'Kam muhim'}
                  {urgencyLevel === 'normal' && 'O\'rtacha'}
                  {urgencyLevel === 'high' && 'Muhim'}
                  {urgencyLevel === 'urgent' && 'Juda muhim'}
                </Badge>
                {urgencyLevel === 'urgent' && (
                  <div className="flex items-center space-x-1 text-red-600">
                    <AlertCircle className="h-4 w-4" />
                    <span className="text-sm">Tezkor ko'rib chiqiladi</span>
                  </div>
                )}
              </div>

              <Button 
                onClick={handleSubmitAll} 
                disabled={createRequestMutation.isPending}
                data-testid="button-submit-all-requests"
                className="bg-primary hover:bg-primary/90"
              >
                {createRequestMutation.isPending ? "Yuborilmoqda..." : `${products.length} ta So'rov Yuborish`}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/fulfillment-calcul.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Calculator, TrendingUp, Target, DollarSign, Zap, Crown, Package, Truck } from "lucide-react";

interface FulfillmentResult {
  tierName: string;
  fixedPayment: number;
  commissionRate: number;
  marketplaceCommissionRate: number;
  commissionAmount: number;
  totalFulfillmentFee: number;
  partnerProfit: number;
  profitPercentage: number;
  netProfit: number;
  marketplaceCommission: number;
  logisticsFee: number;
  sptCost: number;
  tax: number;
}

interface FulfillmentCalculatorProps {
  className?: string;
}

// Haqiqiy Fulfillment model - 4-darajali pricing
const FULFILLMENT_TIERS = {
  starter_pro: {
    name: "Starter Pro",
    fixedPayment: 0, // Risksiz tarif
    commissionRanges: [
      { min: 0, max: 10000000, rate: 45 }, // 45% - 0-10M
      { min: 10000000, max: 50000000, rate: 35 }, // 35% - 10M-50M
      { min: 50000000, max: Infinity, rate: 30 } // 30% - 50M+
    ]
  },
  business_standard: {
    name: "Business Standard", 
    fixedPayment: 3500000, // 3.5M som
    commissionRanges: [
      { min: 0, max: 20000000, rate: 25 }, // 25% - 0-20M
      { min: 20000000, max: 100000000, rate: 20 }, // 20% - 20M-100M  
      { min: 100000000, max: Infinity, rate: 18 } // 18% - 100M+
    ]
  },
  professional_plus: {
    name: "Professional Plus",
    fixedPayment: 6000000, // 6M som
    commissionRanges: [
      { min: 0, max: 50000000, rate: 20 }, // 20% - 0-50M
      { min: 50000000, max: 200000000, rate: 17 }, // 17% - 50M-200M
      { min: 200000000, max: Infinity, rate: 15 } // 15% - 200M+
    ]
  },
  enterprise_elite: {
    name: "Enterprise Elite",
    fixedPayment: 10000000, // 10M som
    commissionRanges: [
      { min: 0, max: 100000000, rate: 18 }, // 18% - 0-100M
      { min: 100000000, max: 500000000, rate: 15 }, // 15% - 100M-500M
      { min: 500000000, max: Infinity, rate: 12 } // 12% - 500M+
    ]
  }
};

// Uzum Market 2024 haqiqiy komissiya stavkalari (kategoriya + narx asosida)
const UZUM_MARKETPLACE_CATEGORIES = {
  // 1-iyundan 25%dan 10%ga tushirilgan kategoriyalar
  electronics: { name: "Elektronika", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 25) },
  baby: { name: "Bolalar kiyim-kechak", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 25) },
  home: { name: "Uy-ro'zg'or mollari", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 25) },
  garden: { name: "Bog' uchun mollar", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 25) },
  appliances: { name: "Konditsionerlar", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 25) },
  stabilizers: { name: "Kuchlanish stabilizatorlari", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 25) },

  // Umumiy kategoriyalar (3-35% oralig'ida)
  clothing: { name: "Kiyim-kechak", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 15 : 30) },
  beauty: { name: "Go'zallik", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 18 : 35) },
  books: { name: "Kitoblar", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 8 : 20) },
  sports: { name: "Sport", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 12 : 25) },
  automotive: { name: "Avto", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 10 : 22) },
  jewelry: { name: "Zargarlik", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 20 : 35) },
  health: { name: "Salomatlik", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 16 : 30) },
  furniture: { name: "Mebel", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 12 : 25) },
  other: { name: "Boshqa", getCommission: (price: number) => price > 5000000 ? 3 : (price > 50000 ? 12 : 25) }
};

// Uzum Market FBO logistika haqlari (rasm asosida to'liq sodda tizim)
const UZUM_LOGISTICS_FEES = {
  kgt: { name: "KGT (99,999 so'm gacha)", fee: 4000 },
  ogt: { name: "O'GT (100k+ so'm)", fee: 6000 },
  ygt_middle: { name: "YGT o'rta (sim-kartalar)", fee: 8000 },
  ygt_large: { name: "YGT katta gabarit", fee: 20000 }
};

export function FulfillmentCalculator({ className }: FulfillmentCalculatorProps) {
  const [selectedTier, setSelectedTier] = useState<keyof typeof FULFILLMENT_TIERS>("starter_pro");
  const [salesInput, setSalesInput] = useState<string>("20,000,000");
  const [costInput, setCostInput] = useState<string>("12,000,000");
  const [quantityInput, setQuantityInput] = useState<string>("1");
  const [logisticsSize, setLogisticsSize] = useState<keyof typeof UZUM_LOGISTICS_FEES>("ogt");
  const [commissionRate, setCommissionRate] = useState<string>("3");
  const [result, setResult] = useState<FulfillmentResult | null>(null);

  // Format number input with commas
  const formatNumberInput = (value: string): string => {
    const numbers = value.replace(/\D/g, '');
    const cleanNumbers = numbers.replace(/^0+/, '') || '0';
    return cleanNumbers.replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  };

  const parseNumberInput = (value: string): number => {
    return parseInt(value.replace(/,/g, '') || '0', 10);
  };

  const handleSalesInputChange = (value: string) => {
    const formatted = formatNumberInput(value);
    setSalesInput(formatted);
  };

  const handleCostInputChange = (value: string) => {
    const formatted = formatNumberInput(value);
    setCostInput(formatted);
  };

  // Haqiqiy Fulfillment hisobi
  const calculateFulfillment = (
    sales: number, 
    cost: number, 
    quantity: number, 
    tier: keyof typeof FULFILLMENT_TIERS,
    logisticsKey: keyof typeof UZUM_LOGISTICS_FEES,
    commissionPercentage: number
  ): FulfillmentResult => {
    const tierConfig = FULFILLMENT_TIERS[tier];
    const marketplaceCommissionRate = commissionPercentage;
    const logisticsPerItem = UZUM_LOGISTICS_FEES[logisticsKey].fee;

    // Marketplace harajatlari (sotuv narxidan hisoblanadi)
    const marketplaceCommission = (sales * marketplaceCommissionRate) / 100;
    const logisticsFee = logisticsPerItem * quantity;
    const sptCost = 2000 * quantity; // SPT harajati har bir mahsulot uchun 2000 som
    const tax = sales * 0.03; // 3% soliq sotuv narxidan

    // Sof foyda = Sotish - Xarid - SPT - Marketplace komissiya - Logistika - Soliq
    const netProfit = sales - cost - sptCost - marketplaceCommission - logisticsFee - tax;

    // Fulfillment komissiyasi sof foydadan
    let commissionRate = 0;
    let commissionAmount = 0;

    if (netProfit > 0) {
      // Sof foyda bo'yicha tegishli tarif aniqlanadi
      const range = tierConfig.commissionRanges.find(r => netProfit >= r.min && netProfit < r.max);
      if (range) {
        commissionRate = range.rate;
        commissionAmount = (netProfit * commissionRate) / 100;
      }
    }

    // Jami fulfillment haqi = Fixed to'lov + sof foydadan komissiya
    const totalFulfillmentFee = tierConfig.fixedPayment + commissionAmount;

    // Hamkor foyda = Sof foyda - Fulfillment haqi
    const partnerProfit = netProfit - totalFulfillmentFee;
    const profitPercentage = sales > 0 ? (partnerProfit / sales) * 100 : 0;

    return {
      tierName: tierConfig.name,
      fixedPayment: tierConfig.fixedPayment,
      commissionRate,
      marketplaceCommissionRate,
      commissionAmount,
      totalFulfillmentFee,
      partnerProfit,
      profitPercentage,
      netProfit,
      marketplaceCommission,
      logisticsFee,
      sptCost,
      tax
    };
  };

  useEffect(() => {
    const sales = parseNumberInput(salesInput);
    const cost = parseNumberInput(costInput);
    const quantity = parseInt(quantityInput || '1', 10);
    const commission = parseFloat(commissionRate) || 0;
    const calculatedResult = calculateFulfillment(sales, cost, quantity, selectedTier, logisticsSize, commission);
    setResult(calculatedResult);
  }, [salesInput, costInput, quantityInput, selectedTier, logisticsSize, commissionRate]);

  const formatSom = (amount: number): string => {
    return new Intl.NumberFormat('uz-UZ').format(Math.round(amount)) + ' so\'m';
  };

  const getTierIcon = (tier: keyof typeof FULFILLMENT_TIERS) => {
    switch (tier) {
      case 'starter_pro': return <Zap className="h-4 w-4" />;
      case 'business_standard': return <Target className="h-4 w-4" />;
      case 'professional_plus': return <TrendingUp className="h-4 w-4" />;
      case 'enterprise_elite': return <Crown className="h-4 w-4" />;
      default: return <Calculator className="h-4 w-4" />;
    }
  };

  const getTierColor = (tier: keyof typeof FULFILLMENT_TIERS) => {
    switch (tier) {
      case 'starter_pro': return 'text-green-600';
      case 'business_standard': return 'text-blue-600';
      case 'professional_plus': return 'text-purple-600';
      case 'enterprise_elite': return 'text-amber-600';
      default: return 'text-gray-600';
    }
  };

  return (
    <Card className={`w-full ${className}`}>
      <CardHeader className="text-center">
        <CardTitle className="flex items-center justify-center gap-2">
          <Calculator className="h-6 w-6 text-primary" />
          Fulfillment Foyda Kalkulyatori
        </CardTitle>
        <CardDescription>
          Haqiqiy Uzum Market FBO modeli asosida - marketplace komissiyalari va logistika harajatlari hisobida
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Tier Selection */}
        <div className="space-y-2">
          <Label>Fulfillment Tarifi</Label>
          <Select value={selectedTier} onValueChange={(value: keyof typeof FULFILLMENT_TIERS) => setSelectedTier(value)}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {Object.entries(FULFILLMENT_TIERS).map(([key, tier]) => (
                <SelectItem key={key} value={key}>
                  <div className="flex items-center gap-2">
                    {getTierIcon(key as keyof typeof FULFILLMENT_TIERS)}
                    <span>{tier.name}</span>
                    <Badge variant="outline" className={getTierColor(key as keyof typeof FULFILLMENT_TIERS)}>
                      {tier.fixedPayment > 0 ? formatSom(tier.fixedPayment) : 'Risksiz'}
                    </Badge>
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="grid md:grid-cols-2 gap-4">
          {/* Product Details */}
          <div className="space-y-4">
            <div className="space-y-2">
              <Label>Sotish Narxi (so'm)</Label>
              <Input
                type="text"
                value={salesInput}
                onChange={(e) => handleSalesInputChange(e.target.value)}
                placeholder="20,000,000"
              />
            </div>

            <div className="space-y-2">
              <Label>Xarid Narxi (so'm)</Label>
              <Input
                type="text"
                value={costInput}
                onChange={(e) => handleCostInputChange(e.target.value)}
                placeholder="12,000,000"
              />
            </div>

            <div className="space-y-2">
              <Label>Miqdor</Label>
              <Input
                type="number"
                value={quantityInput}
                onChange={(e) => setQuantityInput(e.target.value)}
                placeholder="1"
                min="1"
              />
            </div>
          </div>

          {/* Marketplace Settings */}
          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="commissionRate">Marketplace Komissiya (%)</Label>
              <Input
                id="commissionRate"
                type="number"
                value={commissionRate}
                onChange={(e) => setCommissionRate(e.target.value)}
                placeholder="3"
                min="0"
                max="35"
                step="0.1"
                className="w-full"
              />
              <div className="text-xs text-gray-600">
                Marketplace seller panelida ko'rsatilgan aniq komissiya foizini kiriting
              </div>
            </div>

            <div className="space-y-2">
              <Label>Logistika Gabariti</Label>
              <Select value={logisticsSize} onValueChange={(value: keyof typeof UZUM_LOGISTICS_FEES) => setLogisticsSize(value)}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {Object.entries(UZUM_LOGISTICS_FEES).map(([key, log]) => (
                    <SelectItem key={key} value={key}>
                      <div className="flex items-center justify-between w-full">
                        <span>{log.name}</span>
                        <Badge variant="outline">{formatSom(log.fee)}</Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>

        {/* Results */}
        {result && (
          <div className="space-y-4 pt-4 border-t">
            <h3 className="text-lg font-semibold text-center">Foyda Hisobi - {result.tierName}</h3>

            {/* Marketplace Harajatlari */}
            <Card className="bg-red-50 border-red-200">
              <CardHeader className="pb-3">
                <CardTitle className="text-sm text-red-700 flex items-center gap-2">
                  <Package className="h-4 w-4" />
                  Marketplace Harajatlari (hamkordan undiriladi)
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span>Marketplace komissiya ({result.marketplaceCommissionRate}%):</span>
                  <span className="font-medium text-red-600">{formatSom(result.marketplaceCommission)}</span>
                </div>
                <div className="flex justify-between">
                  <span>SPT harajati (2k som/dona):</span>
                  <span className="font-medium text-red-600">{formatSom(result.sptCost)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Logistika ({UZUM_LOGISTICS_FEES[logisticsSize].name}):</span>
                  <span className="font-medium text-red-600">{formatSom(result.logisticsFee)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Soliq (3%):</span>
                  <span className="font-medium text-red-600">{formatSom(result.tax)}</span>
                </div>
                <Separator />
                <div className="flex justify-between font-semibold">
                  <span>Sof Foyda:</span>
                  <span className={result.netProfit > 0 ? "text-green-600" : "text-red-600"}>
                    {formatSom(result.netProfit)}
                  </span>
                </div>
              </CardContent>
            </Card>

            {/* Fulfillment Harajatlari */}
            <Card className="bg-blue-50 border-blue-200">
              <CardHeader className="pb-3">
                <CardTitle className="text-sm text-blue-700 flex items-center gap-2">
                  <Truck className="h-4 w-4" />
                  Fulfillment Harajatlari (bizning xizmat)
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span>Fixed to'lov:</span>
                  <span className="font-medium text-blue-600">{formatSom(result.fixedPayment)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Komissiya sof foydadan ({result.commissionRate}%):</span>
                  <span className="font-medium text-blue-600">{formatSom(result.commissionAmount)}</span>
                </div>
                <Separator />
                <div className="flex justify-between font-semibold">
                  <span>Jami Fulfillment haqi:</span>
                  <span className="text-blue-600">{formatSom(result.totalFulfillmentFee)}</span>
                </div>
              </CardContent>
            </Card>

            {/* Final Result */}
            <Card className="bg-green-50 border-green-200">
              <CardHeader className="pb-3">
                <CardTitle className="text-sm text-green-700 flex items-center gap-2">
                  <DollarSign className="h-4 w-4" />
                  Hamkor Final Foyda
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <div className="flex justify-between items-center">
                  <span className="text-lg font-semibold">Sizning foydangiz:</span>
                  <span className={`text-2xl font-bold ${result.partnerProfit > 0 ? 'text-green-600' : 'text-red-600'}`}>
                    {formatSom(result.partnerProfit)}
                  </span>
                </div>
                <div className="flex justify-between text-sm text-neutral-600">
                  <span>Foyda foizi:</span>
                  <span className={result.profitPercentage > 0 ? 'text-green-600' : 'text-red-600'}>
                    {result.profitPercentage.toFixed(1)}%
                  </span>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/product-request-fo.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Plus, Package } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

interface ProductRequestFormProps {
  onSuccess?: () => void;
}

export function ProductRequestForm({ onSuccess }: ProductRequestFormProps) {
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    productName: "",
    description: "",
    expectedQuantity: "",
    estimatedPrice: "",
    supplierInfo: "",
    urgencyLevel: "normal",
  });

  const createRequestMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      await apiRequest("/api/product-requests", "POST", {
        productName: data.productName,
        description: data.description,
        expectedQuantity: parseInt(data.expectedQuantity) || 1,
        estimatedPrice: parseFloat(data.estimatedPrice) || 0,
        supplierInfo: data.supplierInfo,
        urgencyLevel: data.urgencyLevel,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/product-requests"] });
      toast({
        title: "Muvaffaqiyat",
        description: "Mahsulot so'rovi muvaffaqiyatli yuborildi. Admin ko'rib chiqadi.",
      });
      setFormData({
        productName: "",
        description: "",
        expectedQuantity: "",
        estimatedPrice: "",
        supplierInfo: "",
        urgencyLevel: "normal",
      });
      onSuccess?.();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Xatolik",
        description: "So'rov yuborishda xatolik yuz berdi. Qayta urinib ko'ring.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.productName.trim()) {
      toast({
        title: "Xatolik",
        description: "Mahsulot nomini kiriting",
        variant: "destructive",
      });
      return;
    }
    createRequestMutation.mutate(formData);
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Package className="w-5 h-5" />
          Yangi Mahsulot So'rovi
        </CardTitle>
        <CardDescription>
          Katalogga qo'shmoqchi bo'lgan mahsulot haqida ma'lumot bering
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="productName">Mahsulot Nomi *</Label>
              <Input
                id="productName"
                value={formData.productName}
                onChange={(e) => setFormData(prev => ({ ...prev, productName: e.target.value }))}
                placeholder="Masalan: Xiaomi Redmi Note 12"
                required
                data-testid="input-product-name"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="expectedQuantity">Kutilgan Miqdor</Label>
              <Input
                id="expectedQuantity"
                type="number"
                value={formData.expectedQuantity}
                onChange={(e) => setFormData(prev => ({ ...prev, expectedQuantity: e.target.value }))}
                placeholder="Nechta olib kelmoqchisiz?"
                min="1"
                data-testid="input-expected-quantity"
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="estimatedPrice">Taxminiy Narx (so'm)</Label>
              <Input
                id="estimatedPrice"
                type="number"
                value={formData.estimatedPrice}
                onChange={(e) => setFormData(prev => ({ ...prev, estimatedPrice: e.target.value }))}
                placeholder="Donasi qancha turadi?"
                min="0"
                step="0.01"
                data-testid="input-estimated-price"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="urgencyLevel">Shoshilish Darajasi</Label>
              <select
                id="urgencyLevel"
                value={formData.urgencyLevel}
                onChange={(e) => setFormData(prev => ({ ...prev, urgencyLevel: e.target.value }))}
                className="w-full px-3 py-2 border border-input bg-background rounded-md text-sm"
                data-testid="select-urgency-level"
              >
                <option value="low">Past - 1-2 hafta</option>
                <option value="normal">O'rtacha - 3-5 kun</option>
                <option value="high">Yuqori - 1-2 kun</option>
                <option value="urgent">Shoshilinch - Bugun</option>
              </select>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="description">Mahsulot Tavsifi</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Mahsulot haqida batafsil ma'lumot, xususiyatlari, rangi, o'lchami va boshqalar..."
              className="min-h-[100px]"
              data-testid="textarea-description"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="supplierInfo">Ta'minotchi Ma'lumoti (ixtiyoriy)</Label>
            <Input
              id="supplierInfo"
              value={formData.supplierInfo}
              onChange={(e) => setFormData(prev => ({ ...prev, supplierInfo: e.target.value }))}
              placeholder="Qayerdan olib kelmoqchisiz? Havola yoki ma'lumot..."
              data-testid="input-supplier-info"
            />
          </div>

          <Button 
            type="submit" 
            disabled={createRequestMutation.isPending}
            className="w-full"
            data-testid="button-submit-request"
          >
            <Plus className="w-4 h-4 mr-2" />
            {createRequestMutation.isPending ? "Yuborilmoqda..." : "So'rov Yuborish"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/stats-display.tsx.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { TrendingUp, Package, ShoppingCart, DollarSign, Users, AlertCircle, Target, Star } from "lucide-react";

interface PartnerStats {
  totalOrders: number;
  activeProducts: number;
  totalRevenue: number;
  monthlyRevenue: number;
  commission: number;
}

interface AdminStats {
  totalPartners: number;
  pendingRequests: number;
  totalRevenue: number;
  totalOrders: number;
}

interface StatsDisplayProps {
  stats: PartnerStats | AdminStats;
  type: 'partner' | 'admin';
  className?: string;
}

export function StatsDisplay({ stats, type, className }: StatsDisplayProps) {
  // Handle undefined stats with default values
  if (!stats) {
    stats = type === 'admin' 
      ? { totalPartners: 0, pendingRequests: 0, totalRevenue: 0, totalOrders: 0 }
      : { totalOrders: 0, activeProducts: 0, totalRevenue: 0, monthlyRevenue: 0, commission: 0 };
  }
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('ru-RU', {
      style: 'currency',
      currency: 'RUB',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatNumber = (num: number) => {
    return new Intl.NumberFormat('ru-RU').format(num);
  };

  // Determine tier for partner stats
  const getTier = (revenue: number) => {
    if (revenue >= 50000000) return { name: 'Premium', color: 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300', icon: Star };
    if (revenue >= 25000000) return { name: 'Professional', color: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300', icon: TrendingUp };
    return { name: 'Basic', color: 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300', icon: Target };
  };

  if (type === 'partner') {
    const partnerStats = stats as PartnerStats;
    const tier = getTier(partnerStats.totalRevenue);
    const TierIcon = tier.icon;

    return (
      <div className={`grid gap-4 md:grid-cols-2 lg:grid-cols-4 ${className}`}>
        <Card data-testid="card-total-orders">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Orders</CardTitle>
            <ShoppingCart className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-orders-count">
              {formatNumber(partnerStats.totalOrders)}
            </div>
            <p className="text-xs text-muted-foreground">
              Lifetime orders processed
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-active-products">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Products</CardTitle>
            <Package className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold" data-testid="text-products-count">
              {formatNumber(partnerStats.activeProducts)}
            </div>
            <p className="text-xs text-muted-foreground">
              Approved products
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-total-revenue">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Revenue</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600 dark:text-green-400" data-testid="text-total-revenue">
              {formatCurrency(partnerStats.totalRevenue)}
            </div>
            <div className="flex items-center gap-2 mt-1">
              <Badge className={tier.color}>
                <TierIcon className="h-3 w-3 mr-1" />
                {tier.name}
              </Badge>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-commission">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Commission</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-600 dark:text-blue-400" data-testid="text-commission">
              {formatCurrency(partnerStats.commission)}
            </div>
            <p className="text-xs text-muted-foreground">
              Current month earnings
            </p>
          </CardContent>
        </Card>

        {partnerStats.monthlyRevenue !== undefined && (
          <Card className="md:col-span-2" data-testid="card-monthly-revenue">
            <CardHeader>
              <CardTitle className="text-lg">Monthly Performance</CardTitle>
              <CardDescription>Current month revenue breakdown</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex justify-between items-center">
                <span className="text-sm font-medium">Monthly Revenue:</span>
                <span className="text-lg font-bold" data-testid="text-monthly-revenue">
                  {formatCurrency(partnerStats.monthlyRevenue)}
                </span>
              </div>
              <Separator />
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="text-muted-foreground">Base Payment:</span>
                  <p className="font-semibold">{formatCurrency(5500000)}</p>
                </div>
                <div>
                  <span className="text-muted-foreground">Performance Bonus:</span>
                  <p className="font-semibold">
                    {formatCurrency(Math.max(0, partnerStats.commission - 5500000))}
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    );
  }

  // Admin stats display
  const adminStats = stats as AdminStats;

  return (
    <div className={`grid gap-4 md:grid-cols-2 lg:grid-cols-4 ${className}`}>
      <Card data-testid="card-total-partners">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Total Partners</CardTitle>
          <Users className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold" data-testid="text-partners-count">
            {formatNumber(adminStats.totalPartners)}
          </div>
          <p className="text-xs text-muted-foreground">
            Active partner accounts
          </p>
        </CardContent>
      </Card>

      <Card data-testid="card-pending-requests">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Pending Requests</CardTitle>
          <AlertCircle className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold text-orange-600 dark:text-orange-400" data-testid="text-pending-count">
            {formatNumber(adminStats.pendingRequests)}
          </div>
          <p className="text-xs text-muted-foreground">
            Awaiting approval
          </p>
        </CardContent>
      </Card>

      <Card data-testid="card-platform-revenue">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Platform Revenue</CardTitle>
          <DollarSign className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold text-green-600 dark:text-green-400" data-testid="text-platform-revenue">
            {formatCurrency(adminStats.totalRevenue)}
          </div>
          <p className="text-xs text-muted-foreground">
            Total platform revenue
          </p>
        </CardContent>
      </Card>

      <Card data-testid="card-platform-orders">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Total Orders</CardTitle>
          <ShoppingCart className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold" data-testid="text-platform-orders">
            {formatNumber(adminStats.totalOrders)}
          </div>
          <p className="text-xs text-muted-foreground">
            Platform-wide orders
          </p>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

