import { useEffect, useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { 
  ArrowLeft,
  Store,
  Plus,
  Settings,
  TestTube,
  Trash2,
  CheckCircle,
  XCircle,
  Clock,
  LogOut,
  User
} from "lucide-react";
import { Link, useParams } from "wouter";
import type { MarketplaceIntegration, PartnerWithUser } from "@shared/schema";

export default function AdminMarketplaceManagement() {
  const { user, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const params = useParams<{ partnerId: string }>();
  const partnerId = params.partnerId;

  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [newIntegration, setNewIntegration] = useState({
    marketplace: "",
    storeName: "",
    apiKey: "",
    storeUrl: "",
    additionalConfig: "",
  });

  // Redirect if not authenticated or not admin
  useEffect(() => {
    if (!authLoading && (!user || (user as any).role !== 'admin')) {
      toast({
        title: "Unauthorized",
        description: "Admin access required. Redirecting...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = user ? "/" : "/api/login";
      }, 500);
      return;
    }
  }, [user, authLoading, toast]);

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  // Fetch partner info
  const { data: partner, isLoading: partnerLoading } = useQuery({
    queryKey: [`/api/admin/partners/${partnerId}`],
    enabled: !!partnerId && !!user && (user as any).role === 'admin',
  });

  // Fetch partner's marketplace integrations
  const { data: integrations = [], isLoading } = useQuery({
    queryKey: [`/api/admin/partners/${partnerId}/marketplace-integrations`],
    enabled: !!partnerId && !!user && (user as any).role === 'admin',
  });

  // Create integration mutation
  const createIntegrationMutation = useMutation({
    mutationFn: async (integration: typeof newIntegration) => {
      return await apiRequest("POST", `/api/admin/partners/${partnerId}/marketplace-integrations`, {
        ...integration,
        partnerId,
        additionalConfig: integration.additionalConfig ? JSON.parse(integration.additionalConfig) : {},
      });
    },
    onSuccess: () => {
      toast({
        title: "Muvaffaqiyat",
        description: "Marketplace integratsiyasi yaratildi",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/partners/${partnerId}/marketplace-integrations`] });
      setIsCreateDialogOpen(false);
      setNewIntegration({
        marketplace: "",
        storeName: "",
        apiKey: "",
        storeUrl: "",
        additionalConfig: "",
      });
    },
    onError: (error) => {
      toast({
        title: "Xato",
        description: "Integratsiyani yaratishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  // Delete integration mutation
  const deleteIntegrationMutation = useMutation({
    mutationFn: async (integrationId: string) => {
      return await apiRequest("DELETE", `/api/admin/marketplace-integrations/${integrationId}`);
    },
    onSuccess: () => {
      toast({
        title: "Muvaffaqiyat",
        description: "Integratsiya o'chirildi",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/partners/${partnerId}/marketplace-integrations`] });
    },
    onError: (error) => {
      toast({
        title: "Xato",
        description: "Integratsiyani o'chirishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  // Test connection mutation
  const testConnectionMutation = useMutation({
    mutationFn: async (integrationId: string) => {
      return await apiRequest("POST", `/api/admin/marketplace-integrations/${integrationId}/test-connection`);
    },
    onSuccess: (data) => {
      toast({
        title: "Test muvaffaqiyatli",
        description: `${data.marketplace} ga bog'lanish muvaffaqiyatli`,
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/partners/${partnerId}/marketplace-integrations`] });
    },
    onError: (error) => {
      toast({
        title: "Test xatosi",
        description: "API ga bog'lanishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  const handleCreateIntegration = () => {
    if (!newIntegration.marketplace || !newIntegration.storeName || !newIntegration.apiKey) {
      toast({
        title: "Xato",
        description: "Barcha majburiy maydonlarni to'ldiring",
        variant: "destructive",
      });
      return;
    }

    createIntegrationMutation.mutate(newIntegration);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "success":
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case "failed":
        return <XCircle className="h-4 w-4 text-red-500" />;
      default:
        return <Clock className="h-4 w-4 text-yellow-500" />;
    }
  };

  const getMarketplaceDisplay = (marketplace: string) => {
    switch (marketplace) {
      case "uzum_market":
        return "Uzum Market";
      case "yandex_market":
        return "Yandex Market";
      default:
        return marketplace;
    }
  };

  if (authLoading || partnerLoading) {
    return (
      <div className="p-8">
        <div className="flex items-center justify-center h-64">
          <div className="text-lg">Yuklanmoqda...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-neutral-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Link href="/admin" data-testid="link-back">
                <Button variant="ghost" size="sm" className="text-sm mr-4">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Admin Panel
                </Button>
              </Link>
              <h1 className="text-2xl font-bold text-primary">MarketPlace Pro</h1>
              <span className="ml-4 px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-medium">
                Admin - Marketplace Boshqarish
              </span>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-accent rounded-full"></div>
                <span className="text-sm text-neutral-600">Online</span>
              </div>
              <Button 
                onClick={handleLogout}
                variant="outline"
                size="sm"
                data-testid="button-logout"
              >
                <LogOut className="w-4 h-4 mr-2" />
                Chiqish
              </Button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Partner Info */}
        {partner && (
          <Card className="mb-6">
            <CardHeader>
              <div className="flex items-center space-x-4">
                <Avatar className="h-12 w-12">
                  <AvatarImage src={partner.user?.profileImageUrl || ""} />
                  <AvatarFallback>
                    <User className="h-6 w-6" />
                  </AvatarFallback>
                </Avatar>
                <div>
                  <CardTitle className="text-xl">
                    {partner.user?.firstName || partner.user?.email || 'Hamkor'}
                  </CardTitle>
                  <CardDescription>
                    {partner.businessName && `${partner.businessName} â€¢ `}
                    {partner.user?.email}
                  </CardDescription>
                </div>
              </div>
            </CardHeader>
          </Card>
        )}

        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold">Marketplace Integratsiyalari</h2>
            <p className="text-muted-foreground">
              Hamkor uchun marketplace integratsiyalarini boshqaring
            </p>
          </div>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-add-integration">
                <Plus className="h-4 w-4 mr-2" />
                Yangi integratsiya
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[500px]">
              <DialogHeader>
                <DialogTitle>Yangi Marketplace Integratsiyasi</DialogTitle>
                <DialogDescription>
                  Hamkor uchun yangi marketplace integratsiyasini sozlang
                </DialogDescription>
              </DialogHeader>
              <div className="grid gap-4 py-4">
                <div className="grid gap-2">
                  <Label htmlFor="marketplace">Marketplace</Label>
                  <Select 
                    value={newIntegration.marketplace} 
                    onValueChange={(value) => setNewIntegration({...newIntegration, marketplace: value})}
                  >
                    <SelectTrigger data-testid="select-marketplace">
                      <SelectValue placeholder="Marketplace tanlang" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="uzum_market">Uzum Market</SelectItem>
                      <SelectItem value="yandex_market">Yandex Market</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="grid gap-2">
                  <Label htmlFor="storeName">Do'kon Nomi</Label>
                  <Input
                    id="storeName"
                    placeholder="Do'kon nomini kiriting"
                    value={newIntegration.storeName}
                    onChange={(e) => setNewIntegration({...newIntegration, storeName: e.target.value})}
                    data-testid="input-store-name"
                  />
                </div>
                <div className="grid gap-2">
                  <Label htmlFor="apiKey">API Key</Label>
                  <Input
                    id="apiKey"
                    type="password"
                    placeholder="API kalitini kiriting"
                    value={newIntegration.apiKey}
                    onChange={(e) => setNewIntegration({...newIntegration, apiKey: e.target.value})}
                    data-testid="input-api-key"
                  />
                </div>
                <div className="grid gap-2">
                  <Label htmlFor="storeUrl">Do'kon URL (ixtiyoriy)</Label>
                  <Input
                    id="storeUrl"
                    placeholder="https://seller.uzum.uz/..."
                    value={newIntegration.storeUrl}
                    onChange={(e) => setNewIntegration({...newIntegration, storeUrl: e.target.value})}
                    data-testid="input-store-url"
                  />
                </div>
                <div className="grid gap-2">
                  <Label htmlFor="additionalConfig">Qo'shimcha Sozlamalar (JSON)</Label>
                  <Textarea
                    id="additionalConfig"
                    placeholder='{"warehouse_id": "123", "delivery_type": "standard"}'
                    value={newIntegration.additionalConfig}
                    onChange={(e) => setNewIntegration({...newIntegration, additionalConfig: e.target.value})}
                    data-testid="textarea-additional-config"
                  />
                </div>
              </div>
              <div className="flex justify-end space-x-2">
                <Button
                  variant="outline"
                  onClick={() => setIsCreateDialogOpen(false)}
                  data-testid="button-cancel"
                >
                  Bekor qilish
                </Button>
                <Button
                  onClick={handleCreateIntegration}
                  disabled={createIntegrationMutation.isPending}
                  data-testid="button-create-integration"
                >
                  {createIntegrationMutation.isPending ? "Yaratilmoqda..." : "Yaratish"}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>

        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {[1, 2, 3].map((i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="p-6">
                  <div className="h-6 bg-neutral-200 rounded mb-2"></div>
                  <div className="h-4 bg-neutral-200 rounded mb-4"></div>
                  <div className="h-8 bg-neutral-200 rounded"></div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : integrations.length === 0 ? (
          <Card>
            <CardContent className="text-center py-12">
              <Store className="h-12 w-12 text-neutral-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-neutral-600 mb-2">
                Hech qanday integratsiya yo'q
              </h3>
              <p className="text-neutral-500 mb-4">
                Bu hamkor uchun hali marketplace integratsiyasi yaratilmagan
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {integrations.map((integration: MarketplaceIntegration) => (
              <Card key={integration.id} className="relative" data-testid={`card-integration-${integration.id}`}>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">
                      {getMarketplaceDisplay(integration.marketplace)}
                    </CardTitle>
                    <Badge variant={integration.isActive ? "default" : "secondary"}>
                      {integration.isActive ? "Faol" : "Faol emas"}
                    </Badge>
                  </div>
                  <CardDescription>{integration.storeName}</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-neutral-600">Holat:</span>
                      <div className="flex items-center space-x-1">
                        {getStatusIcon(integration.lastSyncStatus || "pending")}
                        <span className="capitalize">
                          {integration.lastSyncStatus === "success" ? "Muvaffaqiyatli" : 
                           integration.lastSyncStatus === "failed" ? "Xato" : "Kutilmoqda"}
                        </span>
                      </div>
                    </div>
                    {integration.lastSyncAt && (
                      <div className="text-sm text-neutral-600">
                        Oxirgi sync: {new Date(integration.lastSyncAt).toLocaleString("uz-UZ")}
                      </div>
                    )}
                    {integration.storeUrl && (
                      <div className="text-sm">
                        <a 
                          href={integration.storeUrl} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:underline"
                        >
                          Do'konni ochish â†’
                        </a>
                      </div>
                    )}
                  </div>
                  <div className="flex items-center justify-between mt-4">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => testConnectionMutation.mutate(integration.id)}
                      disabled={testConnectionMutation.isPending}
                      data-testid={`button-test-${integration.id}`}
                    >
                      <TestTube className="h-4 w-4 mr-1" />
                      Test
                    </Button>
                    <div className="flex space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        data-testid={`button-settings-${integration.id}`}
                      >
                        <Settings className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => deleteIntegrationMutation.mutate(integration.id)}
                        disabled={deleteIntegrationMutation.isPending}
                        data-testid={`button-delete-${integration.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

server/seedData.ts.content:
import { db } from "./db";
import { users, partners } from "@shared/schema";

export async function seedDemoData() {
  try {
    // Create demo admin user
    const [adminUser] = await db
      .insert(users)
      .values({
        id: "demo_admin",
        email: "admin@marketplace.uz",
        firstName: "Admin",
        lastName: "User",
        role: "admin",
        isApproved: true,
      })
      .onConflictDoNothing()
      .returning();

    // Create demo partner user
    const [partnerUser] = await db
      .insert(users)
      .values({
        id: "demo_partner",
        email: "partner@company.uz",
        firstName: "Demo",
        lastName: "Hamkor",
        role: "partner",
        isApproved: true,
      })
      .onConflictDoNothing()
      .returning();

    // Create partner profile
    if (partnerUser) {
      await db
        .insert(partners)
        .values({
          userId: partnerUser.id,
          businessName: "Demo Biznes",
          description: "Test hamkor kompaniyasi",
          pricingTier: "professional_plus",
          fixedPayment: "4500000.00",
          commissionRate: "15.00",
        })
        .onConflictDoNothing();
    }

    console.log("Demo data seeded successfully");
  } catch (error) {
    console.error("Error seeding demo data:", error);
  }
}

client/src/pages/marketplace-integration.content:
import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Plus, Store, Trash2, TestTube, Settings, CheckCircle, XCircle, Clock, LogOut, BarChart3 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import type { MarketplaceIntegration } from "@shared/schema";

export default function MarketplaceIntegrationPage() {
  const { toast } = useToast();
  const { user } = useAuth();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [newIntegration, setNewIntegration] = useState({
    marketplace: "",
    storeName: "",
    storeId: "",
    apiKey: "",
    secretKey: "",
    autoSync: false,
    syncFrequency: 24,
  });

  // Fetch marketplace integrations
  const { data: integrations = [], isLoading } = useQuery({
    queryKey: ["/api/marketplace-integrations"],
  });

  // Create integration mutation
  const createIntegrationMutation = useMutation({
    mutationFn: async (integrationData: any) => {
      return await apiRequest("/api/marketplace-integrations", {
        method: "POST",
        body: JSON.stringify(integrationData),
      });
    },
    onSuccess: () => {
      toast({
        title: "Muvaffaqiyat",
        description: "Marketplace integratsiyasi muvaffaqiyatli qo'shildi",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/marketplace-integrations"] });
      setIsCreateDialogOpen(false);
      setNewIntegration({
        marketplace: "",
        storeName: "",
        storeId: "",
        apiKey: "",
        secretKey: "",
        autoSync: false,
        syncFrequency: 24,
      });
    },
    onError: (error) => {
      toast({
        title: "Xato",
        description: "Integratsi yaratishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  // Delete integration mutation
  const deleteIntegrationMutation = useMutation({
    mutationFn: async (integrationId: string) => {
      return await apiRequest(`/api/marketplace-integrations/${integrationId}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      toast({
        title: "Muvaffaqiyat",
        description: "Integratsi o'chirildi",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/marketplace-integrations"] });
    },
    onError: (error) => {
      toast({
        title: "Xato",
        description: "Integratsiyani o'chirishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  // Test connection mutation
  const testConnectionMutation = useMutation({
    mutationFn: async (integrationId: string) => {
      return await apiRequest(`/api/marketplace-integrations/${integrationId}/test-connection`, {
        method: "POST",
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Test muvaffaqiyatli",
        description: `${data.marketplace} ga bog'lanish muvaffaqiyatli`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/marketplace-integrations"] });
    },
    onError: (error) => {
      toast({
        title: "Test xatosi",
        description: "API ga bog'lanishda xato yuz berdi",
        variant: "destructive",
      });
    },
  });

  const handleCreateIntegration = () => {
    if (!newIntegration.marketplace || !newIntegration.storeName || !newIntegration.apiKey) {
      toast({
        title: "Xato",
        description: "Barcha majburiy maydonlarni to'ldiring",
        variant: "destructive",
      });
      return;
    }

    createIntegrationMutation.mutate(newIntegration);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "success":
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case "failed":
        return <XCircle className="h-4 w-4 text-red-500" />;
      default:
        return <Clock className="h-4 w-4 text-yellow-500" />;
    }
  };

  const getMarketplaceDisplay = (marketplace: string) => {
    switch (marketplace) {
      case "uzum_market":
        return "Uzum Market";
      case "yandex_market":
        return "Yandex Market";
      default:
        return marketplace;
    }
  };

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  if (isLoading) {
    return (
      <div className="p-8">
        <div className="flex items-center justify-center h-64">
          <div className="text-lg">Yuklanmoqda...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-neutral-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <h1 className="text-2xl font-bold text-primary">MarketPlace Pro</h1>
              <span className="ml-4 px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-medium">
                Marketplace Integratsiya
              </span>
              <nav className="ml-8 flex items-center space-x-4">
                <Link href="/partner" data-testid="link-dashboard">
                  <Button variant="ghost" size="sm" className="text-sm">
                    <BarChart3 className="w-4 h-4 mr-2" />
                    Dashboard
                  </Button>
                </Link>
                <Link href="/marketplace" data-testid="link-marketplace">
                  <Button variant="ghost" size="sm" className="text-sm bg-neutral-100">
                    <Store className="w-4 h-4 mr-2" />
                    Marketplace
                  </Button>
                </Link>
              </nav>
            </div>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-accent rounded-full"></div>
                <span className="text-sm text-neutral-600">Online</span>
              </div>
              <Button 
                onClick={handleLogout}
                variant="outline"
                size="sm"
                data-testid="button-logout"
              >
                <LogOut className="w-4 h-4 mr-2" />
                Chiqish
              </Button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold">Marketplace Integrationa</h1>
          <p className="text-muted-foreground">
            Uzum Market va Yandex Market bilan integratsiyani boshqaring
          </p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-integration">
              <Plus className="h-4 w-4 mr-2" />
              Yangi integratsiya
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[500px]">
            <DialogHeader>
              <DialogTitle>Yangi Marketplace Integratsiyasi</DialogTitle>
              <DialogDescription>
                Uzum Market yoki Yandex Market bilan bog'lanish uchun API ma'lumotlarini kiriting
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4">
              <div className="grid gap-2">
                <Label htmlFor="marketplace">Marketplace</Label>
                <Select
                  value={newIntegration.marketplace}
                  onValueChange={(value) =>
                    setNewIntegration({ ...newIntegration, marketplace: value })
                  }
                >
                  <SelectTrigger data-testid="select-marketplace">
                    <SelectValue placeholder="Marketplace tanlang" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="uzum_market">Uzum Market</SelectItem>
                    <SelectItem value="yandex_market">Yandex Market</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="storeName">Do'kon nomi</Label>
                <Input
                  id="storeName"
                  data-testid="input-store-name"
                  placeholder="Do'kon nomini kiriting"
                  value={newIntegration.storeName}
                  onChange={(e) =>
                    setNewIntegration({ ...newIntegration, storeName: e.target.value })
                  }
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="storeId">Do'kon ID (ixtiyoriy)</Label>
                <Input
                  id="storeId"
                  data-testid="input-store-id"
                  placeholder="Do'kon ID raqamini kiriting"
                  value={newIntegration.storeId}
                  onChange={(e) =>
                    setNewIntegration({ ...newIntegration, storeId: e.target.value })
                  }
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="apiKey">API Key</Label>
                <Input
                  id="apiKey"
                  data-testid="input-api-key"
                  type="password"
                  placeholder="API kalitni kiriting"
                  value={newIntegration.apiKey}
                  onChange={(e) =>
                    setNewIntegration({ ...newIntegration, apiKey: e.target.value })
                  }
                />
              </div>
              <div className="grid gap-2">
                <Label htmlFor="secretKey">Secret Key (ixtiyoriy)</Label>
                <Input
                  id="secretKey"
                  data-testid="input-secret-key"
                  type="password"
                  placeholder="Maxfiy kalitni kiriting"
                  value={newIntegration.secretKey}
                  onChange={(e) =>
                    setNewIntegration({ ...newIntegration, secretKey: e.target.value })
                  }
                />
              </div>
              <div className="flex items-center space-x-2">
                <Switch
                  id="autoSync"
                  data-testid="switch-auto-sync"
                  checked={newIntegration.autoSync}
                  onCheckedChange={(checked) =>
                    setNewIntegration({ ...newIntegration, autoSync: checked })
                  }
                />
                <Label htmlFor="autoSync">Avtomatik sinxronlash</Label>
              </div>
              <div className="grid gap-2">
                <Label htmlFor="syncFrequency">Sinxronlash chastotasi (soat)</Label>
                <Input
                  id="syncFrequency"
                  data-testid="input-sync-frequency"
                  type="number"
                  min="1"
                  max="168"
                  value={newIntegration.syncFrequency}
                  onChange={(e) =>
                    setNewIntegration({
                      ...newIntegration,
                      syncFrequency: parseInt(e.target.value) || 24,
                    })
                  }
                />
              </div>
            </div>
            <div className="flex justify-end gap-3">
              <Button
                variant="outline"
                onClick={() => setIsCreateDialogOpen(false)}
                data-testid="button-cancel"
              >
                Bekor qilish
              </Button>
              <Button
                onClick={handleCreateIntegration}
                disabled={createIntegrationMutation.isPending}
                data-testid="button-create-integration"
              >
                {createIntegrationMutation.isPending ? "Yaratilmoqda..." : "Yaratish"}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-6">
        {integrations.length === 0 ? (
          <Card>
            <CardContent className="flex flex-col items-center justify-center py-12">
              <Store className="h-12 w-12 text-muted-foreground mb-4" />
              <h3 className="text-lg font-semibold mb-2">Hech qanday integratsiya yo'q</h3>
              <p className="text-muted-foreground text-center mb-4">
                Uzum Market yoki Yandex Market bilan integratsiya qo'shing
              </p>
              <Button onClick={() => setIsCreateDialogOpen(true)} data-testid="button-add-first-integration">
                <Plus className="h-4 w-4 mr-2" />
                Birinchi integratsiyani qo'shish
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {integrations.map((integration: MarketplaceIntegration) => (
              <Card key={integration.id} data-testid={`card-integration-${integration.id}`}>
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium">
                    {getMarketplaceDisplay(integration.marketplace)}
                  </CardTitle>
                  <div className="flex items-center space-x-1">
                    {getStatusIcon(integration.lastSyncStatus || 'pending')}
                    <Badge variant={integration.isActive ? "default" : "secondary"}>
                      {integration.isActive ? "Faol" : "Nofaol"}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold mb-2">{integration.storeName}</div>
                  <div className="space-y-2 text-sm text-muted-foreground">
                    <div>Do'kon ID: {integration.storeId || "Kiritilmagan"}</div>
                    <div>Avtomatik sinxronlash: {integration.autoSync ? "Yoqilgan" : "O'chirilgan"}</div>
                    {integration.autoSync && (
                      <div>Sinxronlash chastotasi: {integration.syncFrequency || 24} soat</div>
                    )}
                    <div>Mahsulotlar soni: {integration.totalProductsSynced || 0}</div>
                    <div>Buyurtmalar soni: {integration.totalOrdersImported || 0}</div>
                    {integration.lastSyncAt && (
                      <div>
                        Oxirgi sinxronlash:{" "}
                        {new Date(integration.lastSyncAt).toLocaleDateString()}
                      </div>
                    )}
                  </div>
                  <div className="flex justify-between items-center mt-4 pt-4 border-t">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => testConnectionMutation.mutate(integration.id)}
                      disabled={testConnectionMutation.isPending}
                      data-testid={`button-test-${integration.id}`}
                    >
                      <TestTube className="h-4 w-4 mr-1" />
                      Test
                    </Button>
                    <div className="flex space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        data-testid={`button-settings-${integration.id}`}
                      >
                        <Settings className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => deleteIntegrationMutation.mutate(integration.id)}
                        disabled={deleteIntegrationMutation.isPending}
                        data-testid={`button-delete-${integration.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
    </div>
  );
}

client/src/pages/partner-registration.ts.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Eye, EyeOff, User, ArrowLeft, CheckCircle } from "lucide-react";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";

const registrationSchema = z.object({
  login: z.string().min(3, "Login kamida 3 ta belgi bo'lishi kerak"),
  password: z.string().min(6, "Parol kamida 6 ta belgi bo'lishi kerak"),
  phone: z.string().min(9, "Telefon raqami noto'g'ri"),
  address: z.string().min(5, "Manzil kamida 5 ta belgi bo'lishi kerak"),
  productCategory: z.string().min(1, "Mahsulot toifasini tanlang"),
  investmentAmount: z.string().min(1, "Investitsiya miqdorini kiriting"),
  productQuantity: z.number().min(1, "Mahsulot sonini kiriting"),
});

type RegistrationForm = z.infer<typeof registrationSchema>;

export default function PartnerRegistration() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const form = useForm<RegistrationForm>({
    resolver: zodResolver(registrationSchema),
    defaultValues: {
      login: "",
      password: "",
      phone: "",
      address: "",
      productCategory: "",
      investmentAmount: "",
      productQuantity: 1,
    },
  });

  const onSubmit = async (data: RegistrationForm) => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/partner-registration-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        setIsSuccess(true);
        toast({
          title: "Muvaffaqiyatli ro'yxatdan o'tish",
          description: "So'rovingiz yuborildi. Admin tasdiqlashini kuting.",
        });

        form.reset();
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Ro'yxatdan o'tishda xatolik yuz berdi");
      }
    } catch (error) {
      setError("Tizimda xatolik yuz berdi. Qayta urinib ko'ring.");
    } finally {
      setIsLoading(false);
    }
  };

  if (isSuccess) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center p-4">
        <Card className="w-full max-w-md shadow-2xl border-slate-700">
          <CardHeader className="text-center">
            <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
              <CheckCircle className="w-8 h-8 text-green-600" />
            </div>
            <CardTitle className="text-2xl font-bold text-green-600">Muvaffaqiyatli!</CardTitle>
            <CardDescription className="text-slate-600">
              Ro'yxatdan o'tish so'rovi yuborildi
            </CardDescription>
          </CardHeader>
          <CardContent className="text-center space-y-4">
            <p className="text-slate-600">
              Sizning so'rovingiz admin tomonidan ko'rib chiqiladi. Tasdiqlangandan so'ng SMS orqali xabar beramiz.
            </p>
            <div className="space-y-3">
              <Button
                onClick={() => setLocation('/login')}
                className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
              >
                Tizimga kirish
              </Button>
              <Button
                variant="outline"
                onClick={() => setLocation('/')}
                className="w-full"
              >
                Bosh sahifaga qaytish
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center p-4">
      <Card className="w-full max-w-lg shadow-2xl border-slate-700">
        <CardHeader className="space-y-1 text-center">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setLocation('/')}
            className="absolute top-4 left-4 text-slate-600 hover:text-slate-800"
          >
            <ArrowLeft className="w-4 h-4 mr-1" />
            Orqaga
          </Button>

          <div className="mx-auto w-12 h-12 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center mb-4">
            <User className="w-6 h-6 text-white" />
          </div>
          <CardTitle className="text-2xl font-bold">Hamkor bo'lish</CardTitle>
          <CardDescription className="text-slate-600">
            Bizning hamkor tarmoqimizga qo'shiling
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="login"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Login</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Loginni kiriting"
                          disabled={isLoading}
                          className="bg-slate-50 border-slate-300"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Parol</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input
                            {...field}
                            type={showPassword ? "text" : "password"}
                            placeholder="Parolni kiriting"
                            disabled={isLoading}
                            className="bg-slate-50 border-slate-300 pr-10"
                          />
                          <button
                            type="button"
                            className="absolute inset-y-0 right-0 pr-3 flex items-center"
                            onClick={() => setShowPassword(!showPassword)}
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4 text-slate-400" />
                            ) : (
                              <Eye className="h-4 w-4 text-slate-400" />
                            )}
                          </button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="phone"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Telefon raqami</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        placeholder="+998901234567"
                        disabled={isLoading}
                        className="bg-slate-50 border-slate-300"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="address"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Manzil</FormLabel>
                    <FormControl>
                      <Textarea
                        {...field}
                        placeholder="To'liq manzilni kiriting"
                        disabled={isLoading}
                        className="bg-slate-50 border-slate-300"
                        rows={2}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="productCategory"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Mahsulot toifasi</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger className="bg-slate-50 border-slate-300">
                          <SelectValue placeholder="Mahsulot toifasini tanlang" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Electronics">Elektronika</SelectItem>
                        <SelectItem value="Fashion">Kiyim va moda</SelectItem>
                        <SelectItem value="Home">Uy jihozlari</SelectItem>
                        <SelectItem value="Sports">Sport va faollik</SelectItem>
                        <SelectItem value="Beauty">Go'zallik va parvarish</SelectItem>
                        <SelectItem value="Books">Kitoblar</SelectItem>
                        <SelectItem value="Toys">O'yinchoqlar</SelectItem>
                        <SelectItem value="Food">Oziq-ovqat</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="investmentAmount"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Investitsiya (so'm)</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="1000000"
                          disabled={isLoading}
                          className="bg-slate-50 border-slate-300"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="productQuantity"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Mahsulot soni</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="number"
                          min="1"
                          placeholder="50"
                          disabled={isLoading}
                          className="bg-slate-50 border-slate-300"
                          onChange={(e) => field.onChange(Number(e.target.value))}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              {error && (
                <Alert variant="destructive">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}

              <Button
                type="submit"
                className="w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
                disabled={isLoading}
              >
                {isLoading ? "Yuborilmoqda..." : "Hamkor bo'lish uchun ariza berish"}
              </Button>
            </form>
          </Form>

          <div className="mt-6 pt-6 border-t border-slate-200">
            <div className="text-center">
              <p className="text-sm text-slate-600 mb-4">
                Allaqachon akkauntingiz bormi?
              </p>
              <Button
                variant="outline"
                onClick={() => setLocation('/login')}
                className="w-full"
              >
                Tizimga kirish
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

