// API utility for handling authentication and requests
export const apiRequest = async (url: string, options: RequestInit = {}) => {
  const defaultOptions: RequestInit = {
    credentials: 'include',
    mode: 'cors',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
      'Cache-Control': 'no-cache',
      ...options.headers,
    },
    ...options,
  };

  console.log(`ðŸŒ API Request: ${options.method || 'GET'} ${url}`);
  console.log('ðŸª Current cookies before request:', document.cookie);

  const response = await fetch(url, defaultOptions);

  console.log(`ðŸ“¡ API Response: ${response.status} ${response.statusText}`);
  console.log('ðŸª Cookies after response:', document.cookie);
  console.log('ðŸ“‹ Response headers:', Object.fromEntries(response.headers.entries()));

  return response;
};

export const login = async (username: string, password: string) => {
  const response = await apiRequest('/api/auth/login', {
    method: 'POST',
    body: JSON.stringify({ username, password }),
  });

  const data = await response.json();
  return { response, data };
};

export const getCurrentUser = async () => {
  const response = await apiRequest(`/api/auth/user?t=${Date.now()}`);

  if (response.status === 401) {
    return null;
  }

  if (!response.ok) {
    throw new Error(`User fetch failed: ${response.status}`);
  }

  return await response.json();
};

export const logout = async () => {
  const response = await apiRequest('/api/auth/logout', {
    method: 'POST',
  });

  return response.ok;
};

